import { useEffect,useState } from "react";
import React from "react";
export const HttpContext = React.createContext({
  data: [],
  getData: () => {},
  isLoading: true,
});

function HttpProvider(props) {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  async function fetchData() {
    const response = await fetch(
      "https://api.nytimes.com/svc/topstories/v2/world.json?api-key=9fw5bZorW3brppppfa95DpFlAAKfIQkE"
    );
    if (!response.ok) {
        setLoading(false);
      return;
    }
    const responseData = await response.json();
    const resultList = await responseData.results;
    setData(resultList);
    setLoading(false);
  }
  useEffect(() => {
    fetchData();
  }, [fetchData]);

  return <HttpContext.Provider value={{
      getData:fetchData,
      data:data,
      isLoading:loading,
  }}>{props.children}</HttpContext.Provider>;
}

export default HttpProvider;
